import React, { useState } from 'react';
import toast from 'react-hot-toast';

const CryptoPanel = ({ connectionStatus, keyFingerprint, onInit, onImport, onExport }) => {
  const [password, setPassword] = useState('');
  const [keyB64, setKeyB64] = useState('');
  const [isInitializing, setIsInitializing] = useState(false);
  const [isImporting, setIsImporting] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [showImport, setShowImport] = useState(false);
  const [showExport, setShowExport] = useState(false);
  const [exportedKey, setExportedKey] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleInit = async () => {
    if (!password.trim()) {
      toast.error('Veuillez saisir un mot de passe');
      return;
    }

    if (password.length < 8) {
      toast.error('Le mot de passe doit contenir au moins 8 caractères');
      return;
    }

    setIsInitializing(true);
    try {
      const result = await onInit(password);
      toast.success('Chiffrement initialisé avec succès');
      setPassword('');
      setShowPassword(false);
    } catch (error) {
      toast.error(`Erreur d'initialisation: ${error.message}`);
    } finally {
      setIsInitializing(false);
    }
  };

  const handleImport = async () => {
    if (!keyB64.trim()) {
      toast.error('Veuillez saisir une clé à importer');
      return;
    }

    setIsImporting(true);
    try {
      const result = await onImport(keyB64);
      toast.success('Clé importée avec succès');
      setKeyB64('');
      setShowImport(false);
    } catch (error) {
      toast.error(`Erreur d'import: ${error.message}`);
    } finally {
      setIsImporting(false);
    }
  };

  const handleExport = async () => {
    setIsExporting(true);
    try {
      const result = await onExport();
      setExportedKey(result.key_b64);
      setShowExport(true);
      toast.success('Clé exportée avec succès');
    } catch (error) {
      toast.error(`Erreur d'export: ${error.message}`);
    } finally {
      setIsExporting(false);
    }
  };

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      toast.success('Copié dans le presse-papiers');
    } catch (error) {
      toast.error('Erreur de copie');
    }
  };

  const generateSecurePassword = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';
    let password = '';
    for (let i = 0; i < 16; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setPassword(password);
    setShowPassword(true);
  };

  const isInitialized = connectionStatus.crypto_initialized;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-lg font-semibold text-gray-900 flex items-center">
          <svg className="w-5 h-5 mr-2 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
          </svg>
          Chiffrement
        </h2>
        <div className={`px-2 py-1 rounded-full text-xs font-medium ${
          isInitialized ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'
        }`}>
          {isInitialized ? 'Initialisé' : 'Non initialisé'}
        </div>
      </div>

      {!isInitialized ? (
        <div className="space-y-4">
          {/* Initialisation avec mot de passe */}
          <div className="space-y-3">
            <h3 className="font-medium text-gray-700">Nouvelle clé de chiffrement</h3>

            <div className="relative">
              <input
                type={showPassword ? 'text' : 'password'}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Mot de passe (min. 8 caractères)"
                className="w-full px-3 py-2 pr-20 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isInitializing}
              />
              <div className="absolute right-2 top-1/2 transform -translate-y-1/2 flex space-x-1">
                <button
                  onClick={() => setShowPassword(!showPassword)}
                  className="p-1 text-gray-400 hover:text-gray-600"
                  title={showPassword ? 'Masquer' : 'Afficher'}
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    {showPassword ? (
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.878 9.878L3 3m6.878 6.878L21 21" />
                    ) : (
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                    )}
                  </svg>
                </button>
                <button
                  onClick={generateSecurePassword}
                  className="p-1 text-gray-400 hover:text-gray-600"
                  title="Générer un mot de passe sécurisé"
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                  </svg>
                </button>
              </div>
            </div>

            <button
              onClick={handleInit}
              disabled={!password.trim() || password.length < 8 || isInitializing}
              className={`w-full py-2 px-4 rounded-lg font-medium transition-colors ${
                password.trim() && password.length >= 8 && !isInitializing
                  ? 'bg-green-600 hover:bg-green-700 text-white'
                  : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
            >
              {isInitializing ? (
                <div className="flex items-center justify-center">
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                  Initialisation...
                </div>
              ) : (
                'Initialiser le chiffrement'
              )}
            </button>
          </div>

          {/* Séparateur */}
          <div className="relative">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-t border-gray-300"></div>
            </div>
            <div className="relative flex justify-center text-sm">
              <span className="px-2 bg-white text-gray-500">ou</span>
            </div>
          </div>

          {/* Import de clé */}
          <div className="space-y-3">
            <button
              onClick={() => setShowImport(!showImport)}
              className="flex items-center text-sm text-blue-600 hover:text-blue-800"
            >
              <svg className={`w-4 h-4 mr-1 transform transition-transform ${
                showImport ? 'rotate-90' : ''
              }`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
              Importer une clé existante
            </button>

            {showImport && (
              <div className="space-y-3 p-3 bg-blue-50 rounded-lg">
                <textarea
                  value={keyB64}
                  onChange={(e) => setKeyB64(e.target.value)}
                  placeholder="Collez votre clé de chiffrement ici..."
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent font-mono text-sm"
                  disabled={isImporting}
                />
                <button
                  onClick={handleImport}
                  disabled={!keyB64.trim() || isImporting}
                  className={`w-full py-2 px-4 rounded-lg font-medium transition-colors ${
                    keyB64.trim() && !isImporting
                      ? 'bg-blue-600 hover:bg-blue-700 text-white'
                      : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  }`}
                >
                  {isImporting ? (
                    <div className="flex items-center justify-center">
                      <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                      Import en cours...
                    </div>
                  ) : (
                    'Importer la clé'
                  )}
                </button>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="space-y-4">
          {/* Informations de la clé */}
          <div className="bg-green-50 border border-green-200 rounded-lg p-4">
            <div className="flex items-center mb-2">
              <svg className="w-5 h-5 text-green-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
              </svg>
              <span className="font-medium text-green-800">Chiffrement actif</span>
            </div>
            {keyFingerprint && (
              <div className="text-sm text-green-700">
                <div className="font-mono bg-green-100 px-2 py-1 rounded">
                  Empreinte: {keyFingerprint}
                </div>
              </div>
            )}
          </div>

          {/* Actions */}
          <div className="space-y-2">
            <button
              onClick={handleExport}
              disabled={isExporting}
              className="w-full py-2 px-4 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-medium transition-colors disabled:bg-gray-300 disabled:text-gray-500"
            >
              {isExporting ? (
                <div className="flex items-center justify-center">
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                  Export en cours...
                </div>
              ) : (
                'Exporter la clé'
              )}
            </button>
          </div>
        </div>
      )}

      {/* Modal d'export */}
      {showExport && exportedKey && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900">
                  Clé de chiffrement exportée
                </h3>
                <button
                  onClick={() => setShowExport(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              <div className="space-y-4">
                <div className="p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                  <div className="flex items-center">
                    <svg className="w-5 h-5 text-yellow-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                    </svg>
                    <span className="text-sm text-yellow-800">
                      <strong>Important:</strong> Gardez cette clé en sécurité. Elle est nécessaire pour déchiffrer vos messages.
                    </span>
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Clé de chiffrement (Base64)
                  </label>
                  <div className="relative">
                    <textarea
                      value={exportedKey}
                      readOnly
                      rows={4}
                      className="w-full px-3 py-2 pr-12 border border-gray-300 rounded-lg bg-gray-50 font-mono text-sm"
                    />
                    <button
                      onClick={() => copyToClipboard(exportedKey)}
                      className="absolute top-2 right-2 p-2 text-gray-400 hover:text-gray-600 bg-white rounded border"
                      title="Copier"
                    >
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
                      </svg>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CryptoPanel;
