import React, { useState } from 'react';
import { ApiService } from '../services/api';
import toast from 'react-hot-toast';

const Header = ({ connectionStatus, keyFingerprint }) => {
  const [showSystemInfo, setShowSystemInfo] = useState(false);
  const [systemInfo, setSystemInfo] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSystemInfo = async () => {
    if (showSystemInfo) {
      setShowSystemInfo(false);
      return;
    }

    setLoading(true);
    try {
      const info = await ApiService.getSystemInfo();
      setSystemInfo(info);
      setShowSystemInfo(true);
    } catch (error) {
      toast.error('Erreur lors de la r√©cup√©ration des informations syst√®me');
    } finally {
      setLoading(false);
    }
  };

  const getConnectionStatusColor = () => {
    const { server_connected, lora_sender_connected, lora_receiver_connected, crypto_initialized } = connectionStatus;

    if (server_connected && lora_sender_connected && lora_receiver_connected && crypto_initialized) {
      return 'bg-green-500';
    } else if (server_connected) {
      return 'bg-yellow-500';
    } else {
      return 'bg-red-500';
    }
  };

  const getConnectionStatusText = () => {
    const { server_connected, lora_sender_connected, lora_receiver_connected, crypto_initialized } = connectionStatus;

    if (!server_connected) {
      return 'Serveur d√©connect√©';
    } else if (lora_sender_connected && lora_receiver_connected && crypto_initialized) {
      return 'Syst√®me op√©rationnel';
    } else if (lora_sender_connected && lora_receiver_connected) {
      return 'LoRa connect√© - Crypto requis';
    } else if (crypto_initialized) {
      return 'Crypto initialis√© - LoRa requis';
    } else {
      return 'Configuration requise';
    }
  };

  return (
    <>
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="container mx-auto px-4 py-4 max-w-7xl">
          <div className="flex items-center justify-between">
            {/* Logo et titre */}
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center text-white text-xl font-bold">
                üì°
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">
                  LoRa Secure Communication
                </h1>
                <p className="text-sm text-gray-500">
                  Communication chiffr√©e longue port√©e
                </p>
              </div>
            </div>

            {/* Indicateurs de statut */}
            <div className="flex items-center space-x-4">
              {/* Empreinte de cl√© */}
              {keyFingerprint && (
                <div className="hidden md:flex items-center space-x-2 bg-gray-50 px-3 py-2 rounded-lg">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-xs font-mono text-gray-600">
                    Cl√©: {keyFingerprint.substring(0, 8)}...
                  </span>
                </div>
              )}

              {/* Statut de connexion */}
              <div className="flex items-center space-x-2 bg-gray-50 px-3 py-2 rounded-lg">
                <div className={`w-2 h-2 rounded-full ${getConnectionStatusColor()}`}></div>
                <span className="text-sm font-medium text-gray-700">
                  {getConnectionStatusText()}
                </span>
              </div>

              {/* Bouton d'informations syst√®me */}
              <button
                onClick={handleSystemInfo}
                disabled={loading}
                className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors"
                title="Informations syst√®me"
              >
                {loading ? (
                  <div className="w-5 h-5 border-2 border-gray-300 border-t-gray-600 rounded-full animate-spin"></div>
                ) : (
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                )}
              </button>
            </div>
          </div>

          {/* Indicateurs d√©taill√©s */}
          <div className="mt-4 grid grid-cols-2 md:grid-cols-4 gap-3">
            <div className={`flex items-center space-x-2 px-3 py-2 rounded-lg ${
              connectionStatus.server_connected ? 'bg-green-50 text-green-700' : 'bg-red-50 text-red-700'
            }`}>
              <div className={`w-2 h-2 rounded-full ${
                connectionStatus.server_connected ? 'bg-green-500' : 'bg-red-500'
              }`}></div>
              <span className="text-sm font-medium">Serveur</span>
            </div>

            <div className={`flex items-center space-x-2 px-3 py-2 rounded-lg ${
              connectionStatus.lora_sender_connected ? 'bg-green-50 text-green-700' : 'bg-gray-50 text-gray-500'
            }`}>
              <div className={`w-2 h-2 rounded-full ${
                connectionStatus.lora_sender_connected ? 'bg-green-500' : 'bg-gray-400'
              }`}></div>
              <span className="text-sm font-medium">√âmetteur</span>
            </div>

            <div className={`flex items-center space-x-2 px-3 py-2 rounded-lg ${
              connectionStatus.lora_receiver_connected ? 'bg-green-50 text-green-700' : 'bg-gray-50 text-gray-500'
            }`}>
              <div className={`w-2 h-2 rounded-full ${
                connectionStatus.lora_receiver_connected ? 'bg-green-500' : 'bg-gray-400'
              }`}></div>
              <span className="text-sm font-medium">R√©cepteur</span>
            </div>

            <div className={`flex items-center space-x-2 px-3 py-2 rounded-lg ${
              connectionStatus.crypto_initialized ? 'bg-green-50 text-green-700' : 'bg-gray-50 text-gray-500'
            }`}>
              <div className={`w-2 h-2 rounded-full ${
                connectionStatus.crypto_initialized ? 'bg-green-500' : 'bg-gray-400'
              }`}></div>
              <span className="text-sm font-medium">Chiffrement</span>
            </div>
          </div>
        </div>
      </header>

      {/* Modal d'informations syst√®me */}
      {showSystemInfo && systemInfo && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
